changeSet.sql:
create table scoring.customer ( id uuid not null primary key, personal_id varchar(255) unique, is_ineligible boolean, created_date timestamp(6) with time zone not null, updated_date timestamp(6) with time zone, created_by varchar(255) not null, updated_by varchar(255) );
create table scoring.journal ( id uuid not null primary key, amount numeric(38, 2), term integer, is_approve boolean, created_date timestamp(6) with time zone not null, updated_date timestamp(6) with time zone, created_by varchar(255) not null, details varchar(255), updated_by varchar(255), customer_id_journal uuid not null constraint fk_journal_customer references scoring.customer );
create table scoring_profile ( id uuid not null primary key, financial_capacity integer, is_active boolean, created_date timestamp(6) with time zone not null, updated_date timestamp(6) with time zone, created_by varchar(255) not null, updated_by varchar(255), customer_id_scoring uuid not null constraint fk_scoring_customer references scoring.customer );
